import React, { Component } from 'react' 
import { 
    SubMenuItem
} from './styled-components'

class SubMenu extends Component {
     // State 
     state = {
         pos:0
     }   

     // Render 
     render () {
         const {activeButton} = this.props
         return (
             <React.Fragment>
                 {/* Create Quiz Sub Menu */}
                <div>
                    <SubMenuItem 
                        style={{
                            top: `${10.2+((window.screen.width - 250) / 50)}rem`,
                            zIndex:`${activeButton === 'create-quiz' ? '4' : '-1'}`}}> 
                        <span className="align-self-center create-quiz-submenu-item">
                            Create New
                        </span> 
                    </SubMenuItem>

                    <SubMenuItem 
                    style={{
                        top: `${12.7+((window.screen.width - 250) / 33.33)}rem`,
                        zIndex:`${activeButton === 'create-quiz' ? '4' : '-1'}`}}> 
                        <span className="align-self-center create-quiz-submenu-item">
                            My Quizzes
                        </span> 
                    </SubMenuItem>

                    <SubMenuItem 
                    style={{
                        top: `${15.2+((window.screen.width - 250) / 25)}rem`,
                        zIndex:`${activeButton === 'create-quiz' ? '4' : '-1'}`}}> 
                        <span 
                            className="align-self-center create-quiz-submenu-item"
                            style ={{ marginLeft: `${0.8+(((window.screen.width - 250) / 50)/10)}rem` }}>
                            Question Bank
                        </span> 
                    </SubMenuItem>
                </div>
             </React.Fragment>
         )
     }
}

export default SubMenu
