import React, {Component} from 'react'

class DashboardButton extends Component {
    // State 
    state = {
        active: false,
        toggle: null,
        Zindex: 2,
    }

   // On Click 
   ButtonClick = () => {
        // Toggle Active State
        this.setState(state=>({
            active: !state.active,
        })).then(this.props.Event()).then(this.props.toggleOverlay())

        // Trigger Event
        
       

        console.log(`${this.props.label} ${this.state.active}`)
   }

   // On Update
