    // Render 
    render () {
         return (
            <React.Fragment>
                <div 
                    className={`${this.props.label} dashboard-button`} 
                    onClick={this.ButtonClick}
                    style={{
                        zIndex : `${this.state.active === true ? "5" : "2"}`
                    }}>
                </div>
            </React.Fragment>
        )
    }
}

export default DashboardButton
