import React, { Component } from 'react'
// import {
//     CSSTransition,
//     TransitionGroup
// } from 'react-transition-group'
import { 
    Container, 
    // Button, 
    // ListGroup, 
    // ListGroupItem
} from 'reactstrap'

import { connect } from 'react-redux'
import { Get, Add, Delete } from '../Actions/sampleActions'

class sampleComponent extends Component {
    // Initial State
    state = {
        items : []
    }
    // On Mount
    componentDidMount() {
        const { items } = this.props
        this.setState({
            items: items
        })
    }
    // Render 
    render () {
        const { items } = this.props
        return (
            <Container>
                {/* Add Item Button */}
                    <Button
                        color="dark"
                        className="m-3"
                        >Add Item</Button>
                {/* List Group */}
                <ListGroup>
                    <TransitionGroup>
                        { items.map( ({id,name}) => (
                            <CSSTransition key={id} timeout={500}>
                                <ListGroupItem>
                                    {/* Delete Button */}
                                        <Button
                                            color='warning'
                                            className="mr-auto ml-3">
                                                &times;
                                            </Button>
                                    {/* Item Name */}
                                        { name }
                                </ListGroupItem>
                            </CSSTransition>
                        ))}
                    </TransitionGroup>
                </ListGroup>
            </Container>
        )
    }
}

const mapStateToProps = state => ({
    items: [...state.sampleItem.list]
})

export default connect(mapStateToProps, { Get, Add, Delete })(sampleComponent)
